import React, { useState } from 'react';
import './App.css';

const App = () => {
  // États initaux
  const [formData, setFormData] = useState({
    // Numériques
    kilometrage: '',
    anneeModele: '',
    nombrePortes: '',
    puissanceFiscale: '',

    // Catégoriels
    premiereMain: false,
    typeBoite: 'manuelle',
    typeCarburant: 'essence',
    marque: '',
    modele: '',
    etat: 'Très bon',
    origine: 'Wilmaya'
  });

  const [prediction, setPrediction] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  // Dans votre composant React
const [error, setError] = useState(null);

const handleSubmit = async (e) => {
  e.preventDefault();
  setError(null);
  setIsLoading(true);

  try {
    // Validation des données
    if (!formData.kilometrage || !formData.anneeModele) {
      throw new Error('Veuillez remplir tous les champs obligatoires');
    }

    const response = await fetch(`${process.env.REACT_APP_API_URL}/predict`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-API-KEY': process.env.REACT_APP_API_KEY
      },
      body: JSON.stringify({
        // ... données du formulaire
      })
    });

    const data = await response.json();

    if (!response.ok) {
      throw new Error(data.message || 'Erreur du serveur');
    }

    setPrediction({
      value: data.prediction,
      confidence: data.confidence // Si votre API fournit un score de confiance
    });

  } catch (err) {
    setError(err.message);
  } finally {
    setIsLoading(false);
  }
};

// Ajouter dans le rendu :
{error && <div className="error-message">{error}</div>}

  // Options pour les select
  const etatOptions = ['Très bon', 'Bon', 'Moyen', 'Mauvais'];
  const typeBoiteOptions = ['manuelle', 'automatique'];
  const carburantOptions = ['essence', 'diesel', 'électrique', 'hybride'];
  const porteOptions = ['3', '5'];

  return (
    <div className="app">
      <h1>Prédiction du prix de voiture</h1>

      <form onSubmit={handleSubmit}>
        <div className="form-columns">
          {/* Colonne Numérique */}
          <div className="form-column">
            <h3>Caractéristiques numériques</h3>

            <div className="form-group">
              <label>Kilométrage</label>
              <input
                type="number"
                name="kilometrage"
                value={formData.kilometrage}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Année-Modèle</label>
              <input
                type="number"
                name="anneeModele"
                value={formData.anneeModele}
                onChange={handleChange}
                min="1990"
                max={new Date().getFullYear()}
                required
              />
            </div>

            <div className="form-group">
              <label>Nombre-de-portes</label>
              <select
                name="nombrePortes"
                value={formData.nombrePortes}
                onChange={handleChange}
                required
              >
                <option value="">Sélectionnez</option>
                {porteOptions.map(opt => (
                  <option key={opt} value={opt}>{opt} portes</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Puissance-fiscale (CV)</label>
              <input
                type="number"
                name="puissanceFiscale"
                value={formData.puissanceFiscale}
                onChange={handleChange}
                min="1"
                required
              />
            </div>
          </div>

          {/* Colonne Catégorielle */}
          <div className="form-column">
            <h3>Caractéristiques catégorielles</h3>

            <div className="form-group checkbox-group">
              <input
                type="checkbox"
                id="premiereMain"
                name="premiereMain"
                checked={formData.premiereMain}
                onChange={handleChange}
              />
              <label htmlFor="premiereMain">Première-main</label>
            </div>

            <div className="form-group">
              <label>Type boîte</label>
              <select
                name="typeBoite"
                value={formData.typeBoite}
                onChange={handleChange}
                required
              >
                {typeBoiteOptions.map(opt => (
                  <option key={opt} value={opt}>{opt.charAt(0).toUpperCase() + opt.slice(1)}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Type carburant</label>
              <select
                name="typeCarburant"
                value={formData.typeCarburant}
                onChange={handleChange}
                required
              >
                {carburantOptions.map(opt => (
                  <option key={opt} value={opt}>{opt.charAt(0).toUpperCase() + opt.slice(1)}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Marque</label>
              <input
                type="text"
                name="marque"
                value={formData.marque}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Modèle</label>
              <input
                type="text"
                name="modele"
                value={formData.modele}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>État</label>
              <select
                name="etat"
                value={formData.etat}
                onChange={handleChange}
                required
              >
                {etatOptions.map(opt => (
                  <option key={opt} value={opt}>{opt}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Origine</label>
              <input
                type="text"
                name="origine"
                value={formData.origine}
                onChange={handleChange}
                required
              />
            </div>
          </div>
        </div>

        <button type="submit" disabled={isLoading} className="submit-btn">
          {isLoading ? 'Calcul en cours...' : 'Prédire le prix'}
        </button>
      </form>

      {prediction && (
        <div className="prediction-result">
          <h2>Résultat de la prédiction</h2>
          <p>Prix estimé : <strong>{prediction.toLocaleString()} €</strong></p>
        </div>
      )}
    </div>
  );
};

export default App;
