import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_CONFIG = {
  BASE_URL: 'http://127.0.0.1:8000',
  ENDPOINTS: {
    BRANDS: '/brands',
    MODELS: '/models',  // Note: Nous ajouterons la marque dans l'URL dynamiquement
    PREDICT: '/predict'
  }
};

function CarPricePredictor() {
  // États du formulaire
  const [formData, setFormData] = useState({
    marke: '',          // Correspond à 'marke' dans FastAPI
    model: '',          // Correspond à 'model' dans FastAPI
    Année_Modèle: '',   // Correspond à 'Année_Modèle' dans FastAPI
    kilometrage: '',    // Correspond à 'kilometrage' dans FastAPI
    Nombre_de_portes: '',
    Puissance_fiscale: '',
    Première_main: 'Non',
    type_boit: 'manuelle',
    type_carburant: 'essence',
    État: 'Occasion',
    Origine: 'France'
  });

  // États de l'application
  const [prediction, setPrediction] = useState(null);
  const [brands, setBrands] = useState([]);
  const [models, setModels] = useState([]);
  const [loading, setLoading] = useState({
    brands: false,
    models: false,
    prediction: false
  });
  const [errors, setErrors] = useState({});

  // 1. Chargement des marques au premier rendu
  useEffect(() => {
    const fetchBrands = async () => {
      setLoading(prev => ({ ...prev, brands: true }));
      try {
        const response = await axios.get(`${API_CONFIG.BASE_URL}${API_CONFIG.ENDPOINTS.BRANDS}`);
        setBrands(response.data.brands);
      } catch (error) {
        console.error("Erreur lors du chargement des marques:", error);
        setErrors(prev => ({ ...prev, api: "Impossible de charger les marques" }));
      } finally {
        setLoading(prev => ({ ...prev, brands: false }));
      }
    };

    fetchBrands();
  }, []);

  // 2. Chargement des modèles quand la marque change
  useEffect(() => {
    if (formData.marke) {
      const fetchModels = async () => {
        setLoading(prev => ({ ...prev, models: true }));
        try {
          const response = await axios.get(
            `${API_CONFIG.BASE_URL}${API_CONFIG.ENDPOINTS.MODELS}/${formData.marke}`
          );
          setModels(response.data.models);
        } catch (error) {
          console.error("Erreur lors du chargement des modèles:", error);
          setErrors(prev => ({ ...prev, api: "Impossible de charger les modèles" }));
        } finally {
          setLoading(prev => ({ ...prev, models: false }));
        }
      };

      fetchModels();
    } else {
      setModels([]);
    }
  }, [formData.marke]);

  // 3. Validation du formulaire
  const validateForm = () => {
    const newErrors = {};

    if (!formData.marke) newErrors.marke = 'Sélectionnez une marque';
    if (!formData.model) newErrors.model = 'Sélectionnez un modèle';
    if (!formData.Année_Modèle || formData.Année_Modèle < 1990 || formData.Année_Modèle > new Date().getFullYear()) {
      newErrors.Année_Modèle = 'Année invalide (1990-actuel)';
    }
    if (!formData.kilometrage || formData.kilometrage < 0) {
      newErrors.kilometrage = 'Kilométrage invalide';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // 4. Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(prev => ({ ...prev, prediction: true }));
    setPrediction(null);

    try {
      const response = await axios.post(
        `${API_CONFIG.BASE_URL}${API_CONFIG.ENDPOINTS.PREDICT}`,
        formData
      );

      setPrediction({
        price: response.data.predicted_price,
        confidence: response.data.confidence || null
      });
    } catch (error) {
      console.error("Erreur lors de la prédiction:", error);
      let errorMsg = "Erreur lors du calcul";

      if (error.response) {
        errorMsg = error.response.data.detail ||
                  `Erreur ${error.response.status}: ${error.response.statusText}`;
      }

      setErrors(prev => ({ ...prev, api: errorMsg }));
    } finally {
      setLoading(prev => ({ ...prev, prediction: false }));
    }
  };

  // 5. Gestion des changements
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Réinitialiser le modèle si la marque change
    if (name === 'marke') {
      setFormData(prev => ({ ...prev, model: '' }));
    }
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Estimateur de Prix Voiture</h1>
        <p>Obtenez une estimation précise en quelques secondes</p>
      </header>

      {errors.api && <div className="error-banner">{errors.api}</div>}

      <form onSubmit={handleSubmit} className="prediction-form">
        {/* Sélection Marque */}
        <div className={`form-group ${errors.marke ? 'has-error' : ''}`}>
          <label>Marque *</label>
          <select
            name="marke"
            value={formData.marke}
            onChange={handleChange}
            disabled={loading.brands}
          >
            <option value="">{loading.brands ? 'Chargement...' : '-- Sélectionnez --'}</option>
            {brands.map((brand, index) => (
              <option key={index} value={brand}>
                {brand}
              </option>
            ))}
          </select>
          {errors.marke && <span className="error-text">{errors.marke}</span>}
        </div>

        {/* Sélection Modèle */}
        <div className={`form-group ${errors.model ? 'has-error' : ''}`}>
          <label>Modèle *</label>
          <select
            name="model"
            value={formData.model}
            onChange={handleChange}
            disabled={!formData.marke || loading.models}
          >
            <option value="">{loading.models ? 'Chargement...' : '-- Sélectionnez --'}</option>
            {models.map((model, index) => (
              <option key={index} value={model}>
                {model}
              </option>
            ))}
          </select>
          {errors.model && <span className="error-text">{errors.model}</span>}
        </div>

        {/* Année */}
        <div className={`form-group ${errors.Année_Modèle ? 'has-error' : ''}`}>
          <label>Année de fabrication *</label>
          <input
            type="number"
            name="Année_Modèle"
            value={formData.Année_Modèle}
            onChange={handleChange}
            min="1990"
            max={new Date().getFullYear()}
            placeholder="Ex: 2020"
          />
          {errors.Année_Modèle && <span className="error-text">{errors.Année_Modèle}</span>}
        </div>

        {/* Kilométrage */}
        <div className={`form-group ${errors.kilometrage ? 'has-error' : ''}`}>
          <label>Kilométrage (km) *</label>
          <input
            type="number"
            name="kilometrage"
            value={formData.kilometrage}
            onChange={handleChange}
            min="0"
            placeholder="Ex: 50000"
          />
          {errors.kilometrage && <span className="error-text">{errors.kilometrage}</span>}
        </div>

        {/* Nombre de portes */}
        <div className="form-group">
          <label>Nombre de portes *</label>
          <select
            name="Nombre_de_portes"
            value={formData.Nombre_de_portes}
            onChange={handleChange}
          >
            <option value="">-- Sélectionnez --</option>
            <option value="2">2 portes</option>
            <option value="3">3 portes</option>
            <option value="4">4 portes</option>
            <option value="5">5 portes</option>
          </select>
        </div>

        {/* Puissance fiscale */}
        <div className="form-group">
          <label>Puissance fiscale (CV) *</label>
          <input
            type="number"
            name="Puissance_fiscale"
            value={formData.Puissance_fiscale}
            onChange={handleChange}
            min="0"
            step="0.1"
            placeholder="Ex: 5.5"
          />
        </div>

        {/* Première main */}
        <div className="form-group">
          <label>Première main</label>
          <select
            name="Première_main"
            value={formData.Première_main}
            onChange={handleChange}
          >
            <option value="Oui">Oui</option>
            <option value="Non">Non</option>
          </select>
        </div>

        {/* Type de boîte */}
        <div className="form-group">
          <label>Type de boîte</label>
          <select
            name="type_boit"
            value={formData.type_boit}
            onChange={handleChange}
          >
            <option value="manuelle">Manuelle</option>
            <option value="automatique">Automatique</option>
          </select>
        </div>

        {/* Type de carburant */}
        <div className="form-group">
          <label>Carburant</label>
          <select
            name="type_carburant"
            value={formData.type_carburant}
            onChange={handleChange}
          >
            <option value="essence">Essence</option>
            <option value="diesel">Diesel</option>
            <option value="electrique">Électrique</option>
            <option value="hybride">Hybride</option>
          </select>
        </div>

        <button
          type="submit"
          className="submit-btn"
          disabled={loading.prediction}
        >
          {loading.prediction ? (
            <span className="loading-indicator">Calcul en cours...</span>
          ) : (
            'Estimer le prix'
          )}
        </button>
      </form>

      {/* Affichage des résultats */}
      {prediction && (
        <div className="result-card">
          <h2>Résultat de l'estimation</h2>
          <div className="price-estimate">
            <span className="price">{prediction.price.toLocaleString('fr-FR')} €</span>
            {prediction.confidence && (
              <span className="confidence">Fiabilité: {prediction.confidence}/10</span>
            )}
          </div>
          <p className="disclaimer">
            Cette estimation est fournie à titre indicatif et ne constitue pas une offre ferme.
          </p>
        </div>
      )}
    </div>
  );
}

export default CarPricePredictor;
