import React, { useState } from 'react';
import './App.css';

const App = () => {
  // États du formulaire (correspondance exacte avec le backend)
  const [formData, setFormData] = useState({
    kilometrage: '',
    Année_Modèle: '',
    Nombre_de_portes: '',
    Puissance_fiscale: '',
    Première_main: 'Non',
    type_boit: 'manuelle',
    type_carburant: 'essence',
    marke: '',
    model: '',
    État: 'Très bon',
    Origine: 'Wilmaya'
  });

  const [prediction, setPrediction] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Options pour les selects
  const options = {
    etat: ['Très bon', 'Bon', 'Moyen', 'Mauvais','Excellent'],
    typeBoite: ['manuelle', 'automatique'],
    typeCarburant: ['essence', 'diesel', 'électrique', 'hybride'],
    nombrePortes: ['3', '5'],
    premiereMain: ['Oui', 'Non']
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? (checked ? 'Oui' : 'Non') : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);

    try {
      // Conversion des types pour correspondre au schéma FastAPI
      const payload = {
        ...formData,
        kilometrage: parseFloat(formData.kilometrage) || 0,
        Année_Modèle: parseInt(formData.Année_Modèle) || 2020,
        Nombre_de_portes: parseInt(formData.Nombre_de_portes) || 5,
        Puissance_fiscale: parseFloat(formData.Puissance_fiscale) || 5
      };

      const response = await fetch('http://localhost:8000/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || `Erreur ${response.status}`);
      }

      setPrediction(data.predicted_price);

    } catch (err) {
      console.error('Erreur API:', err);
      setError(err.message || 'Erreur lors de la prédiction');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app">
      <h1>Prédiction du prix de voiture</h1>

      {error && (
        <div className="error">
          {typeof error === 'string' ? error : JSON.stringify(error)}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div className="form-columns">
          {/* Colonne des champs numériques */}
          <div className="form-column">
            <h3>Informations techniques</h3>

            <div className="form-group">
              <label>Kilométrage *</label>
              <input
                type="number"
                name="kilometrage"
                value={formData.kilometrage}
                onChange={handleChange}
                min="0"
                required
              />
            </div>

            <div className="form-group">
              <label>Année Modèle *</label>
              <input
                type="number"
                name="Année_Modèle"
                value={formData.Année_Modèle}
                onChange={handleChange}
                min="1990"
                max="2025"
                required
              />
            </div>

            <div className="form-group">
              <label>Nombre de portes</label>
              <select
                name="Nombre_de_portes"
                value={formData.Nombre_de_portes}
                onChange={handleChange}
              >
                <option value="">Sélectionnez</option>
                {options.nombrePortes.map(opt => (
                  <option key={opt} value={opt}>{opt}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Puissance fiscale</label>
              <input
                type="number"
                name="Puissance_fiscale"
                value={formData.Puissance_fiscale}
                onChange={handleChange}
                min="1"
                step="0.1"
              />
            </div>
          </div>

          {/* Colonne des caractéristiques */}
          <div className="form-column">
            <h3>Caractéristiques</h3>

            <div className="form-group checkbox-group">
              <input
                type="checkbox"
                id="Première_main"
                name="Première_main"
                checked={formData.Première_main === 'Oui'}
                onChange={handleChange}
              />
              <label htmlFor="Première_main">Première main</label>
            </div>

            <div className="form-group">
              <label>Type boîte</label>
              <select
                name="type_boit"
                value={formData.type_boit}
                onChange={handleChange}
              >
                {options.typeBoite.map(opt => (
                  <option key={opt} value={opt}>
                    {opt.charAt(0).toUpperCase() + opt.slice(1)}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Type carburant</label>
              <select
                name="type_carburant"
                value={formData.type_carburant}
                onChange={handleChange}
              >
                {options.typeCarburant.map(opt => (
                  <option key={opt} value={opt}>
                    {opt.charAt(0).toUpperCase() + opt.slice(1)}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Marque *</label>
              <input
                type="text"
                name="marke"
                value={formData.marke}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>Modèle</label>
              <input
                type="text"
                name="model"
                value={formData.model}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label>État</label>
              <select
                name="État"
                value={formData.État}
                onChange={handleChange}
              >
                {options.etat.map(opt => (
                  <option key={opt} value={opt}>{opt}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Origine</label>
              <input
                type="text"
                name="Origine"
                value={formData.Origine}
                onChange={handleChange}
              />
            </div>
          </div>
        </div>

        <button
          type="submit"
          className="submit-btn"
          disabled={isLoading}
        >
          {isLoading ? 'Calcul en cours...' : 'Prédire le prix'}
        </button>
      </form>

      {prediction !== null && (
        <div className="prediction-result">
          <h2>Résultat de la prédiction</h2>
          <p className="price">{prediction.toLocaleString('fr-FR')} MAD</p>
          <p className="disclaimer">* Estimation basée sur les informations fournies</p>
        </div>
      )}
    </div>
  );
};

export default App;
