import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = 'https://votre-api.com'; // Remplacez par votre URL

function App() {
  // États du formulaire
  const [formData, setFormData] = useState({
    brand: '',
    model: '',
    year: '',
    mileage: '',
    fuel: 'essence',
    transmission: 'manual'
  });

  // États de l'application
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [brands, setBrands] = useState([]);
  const [models, setModels] = useState([]);
  const [brandLoading, setBrandLoading] = useState(false);
  const [modelLoading, setModelLoading] = useState(false);

  // Charger les marques au démarrage
  useEffect(() => {
    const fetchBrands = async () => {
      setBrandLoading(true);
      try {
        const response = await axios.get(`${API_URL}/brands`);
        setBrands(response.data);
      } catch (error) {
        console.error("Erreur chargement marques:", error);
      } finally {
        setBrandLoading(false);
      }
    };
    fetchBrands();
  }, []);

  // Charger les modèles quand la marque change
  useEffect(() => {
    if (formData.brand) {
      const fetchModels = async () => {
        setModelLoading(true);
        try {
          const response = await axios.get(`${API_URL}/models?brand=${formData.brand}`);
          setModels(response.data);
        } catch (error) {
          console.error("Erreur chargement modèles:", error);
        } finally {
          setModelLoading(false);
        }
      };
      fetchModels();
    }
  }, [formData.brand]);

  // Validation
  const validate = () => {
    const newErrors = {};
    if (!formData.brand) newErrors.brand = 'Sélectionnez une marque';
    if (!formData.model) newErrors.model = 'Sélectionnez un modèle';
    if (!formData.year || formData.year < 1990 || formData.year > new Date().getFullYear()) {
      newErrors.year = 'Année invalide';
    }
    if (!formData.mileage || formData.mileage < 0) {
      newErrors.mileage = 'Kilométrage invalide';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    setLoading(true);
    try {
      const response = await axios.post(`${API_URL}/predict`, formData);
      setPrediction(response.data.predicted_price);
    } catch (error) {
      console.error("Erreur prédiction:", error);
      alert(`Erreur: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Estimation de prix de voiture</h1>
        <p>Obtenez une estimation précise en temps réel</p>
      </header>

      <form onSubmit={handleSubmit} className="car-form">
        {/* Champ Marque */}
        <div className={`form-group ${errors.brand ? 'error' : ''}`}>
          <label>Marque *</label>
          <select
            name="brand"
            value={formData.brand}
            onChange={handleChange}
            disabled={brandLoading}
          >
            <option value="">{brandLoading ? 'Chargement...' : 'Sélectionnez'}</option>
            {brands.map(brand => (
              <option key={brand.id} value={brand.id}>{brand.name}</option>
            ))}
          </select>
          {errors.brand && <span className="error-message">{errors.brand}</span>}
        </div>

        {/* Champ Modèle */}
        <div className={`form-group ${errors.model ? 'error' : ''}`}>
          <label>Modèle *</label>
          <select
            name="model"
            value={formData.model}
            onChange={handleChange}
            disabled={!formData.brand || modelLoading}
          >
            <option value="">{modelLoading ? 'Chargement...' : 'Sélectionnez'}</option>
            {models.map(model => (
              <option key={model.id} value={model.id}>{model.name}</option>
            ))}
          </select>
          {errors.model && <span className="error-message">{errors.model}</span>}
        </div>

        {/* Champ Année */}
        <div className={`form-group ${errors.year ? 'error' : ''}`}>
          <label>Année *</label>
          <input
            type="number"
            name="year"
            value={formData.year}
            onChange={handleChange}
            min="1990"
            max={new Date().getFullYear()}
            placeholder="Ex: 2018"
          />
          {errors.year && <span className="error-message">{errors.year}</span>}
        </div>

        {/* Champ Kilométrage */}
        <div className={`form-group ${errors.mileage ? 'error' : ''}`}>
          <label>Kilométrage *</label>
          <input
            type="number"
            name="mileage"
            value={formData.mileage}
            onChange={handleChange}
            min="0"
            placeholder="Ex: 50000"
          />
          {errors.mileage && <span className="error-message">{errors.mileage}</span>}
        </div>

        {/* Champ Carburant */}
        <div className="form-group">
          <label>Carburant *</label>
          <select name="fuel" value={formData.fuel} onChange={handleChange}>
            <option value="essence">Essence</option>
            <option value="diesel">Diesel</option>
            <option value="electric">Électrique</option>
            <option value="hybrid">Hybride</option>
          </select>
        </div>

        {/* Champ Transmission */}
        <div className="form-group">
          <label>Transmission *</label>
          <select name="transmission" value={formData.transmission} onChange={handleChange}>
            <option value="manual">Manuelle</option>
            <option value="automatic">Automatique</option>
          </select>
        </div>

        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? 'Calcul en cours...' : 'Obtenir une estimation'}
        </button>
      </form>

      {prediction && (
        <div className="result-container">
          <h2>Résultat de l'estimation</h2>
          <p className="price">{Math.round(prediction).toLocaleString('fr-FR')} €</p>
          <p className="disclaimer">* Prix estimé basé sur les informations fournies</p>
        </div>
      )}
    </div>
  );
}

export default App;
